import React from 'react'
import { useState, useRef } from 'react'

import './Numberbaseball.css'
export default function Numberbaseball() {
    const [word, setWord] = useState();
    const [inputWord, setInputWord] = useState("");
    const [numArray, setNumArray] = useState([0, 0, 0, 0, 0, 0, 0, 0, 0]);
    const [answerArray, setAnswerArray] = useState([0,0,0]);
    function MakeNumber() {
        setNumArray([0, 0, 0, 0, 0, 0, 0, 0, 0]);
        let WordArray = [];
        console.log("gd");
        for (let i = 1; i < 5;) {
            let iNum = Math.floor(Math.random() * (numArray.length - 1) + 1);
            if (numArray[iNum] === 0) {
                numArray[iNum] = i++;
                WordArray.push(iNum);
                console.log(numArray);
            }
        }
        setWord(WordArray);
    }

    if (word === undefined) {
        MakeNumber();
    }

    function CheckNumber() {
        setAnswerArray([0,0,0]);
        for (let i = 0; i < 4; i++) {
            let iIdx = numArray.indexOf(inputWord[i]);
            switch (iIdx) {
                case i:
                    answerArray[0]++;
                    break;
                case -1:
                    answerArray[2]++;
                    break;
                default :
                answerArray[1]++;
                    break;
            }
        }
    }
    function onClickButtonAnswer() {
        CheckNumber();
    }
    function onClickButtonReset() {
        MakeNumber();
        //setInputWord("여기에 입력하여주세요!");
    }
    function onClickInput() {
        //setInputWord("");
    }
    function onChangeInput(e) {
        setInputWord(e.target.value);
    }
    return (
        <element id="Numberbaseball">
            <div>현재 숫자 :: {word}</div>
            <input onClick={onClickInput} value={inputWord} onChange={onChangeInput} maxLength='4' />
            <button onClick={CheckNumber}>GO!</button>
            <button onClick={onClickButtonReset}>초기화</button>
    <div>{answerArray[0]}Strike! {answerArray[1]}Ball! {answerArray[2]}Out!</div>
        </element>
    )
}
