  return (
    <nav>
      <ul>
        <li>1. test</li>
        <li>2. test</li>
        <li>3. test</li>
        <li>4. test</li>
      </ul>
    </nav>
  )
}

function App(props) {
const [date, setDate] = useState("test");
function Change() {
  setDate(new Date().toLocaleTimeString());
  componentWIllUnmount();
}
function componentDidMount() {
  this.timeID = setInterval(
    () => Change(), 1000
  )
}

function componentWIllUnmount() {
  clearInterval(this.timeID)
}
  return (
    <div className="App">
      Hello React
      <script>
      </script>
    <h3>현재 시각은 [componentDidMount()]</h3>
      <Nav />
    </div>
  );
}

export default App;
