import React from 'react'

import './Menu.css';

import { useState } from 'react';

function Menu(props) {
    //props.onClickNav();
    //여기서 STATE를 만들고
    const [menuNum, setmenuNum] = useState("-1");
    function onClick(i) {
        //여기서 STATE 값을 i로 바꾸고
        //밑에 버튼 태그의 onCLickNav(STATE)이렇게 넘긴다.
        setmenuNum(i);
    }

    return (
        <button onClick = {props.onClickNav(menuNum)}>
            <a href={props.Address}>
                <div>
                    {props.Content}
                </div>
            </a>
        </button>
    )
}

export default Menu
