import React from 'react'
import { useState, useRef } from 'react'

import './Numberbaseball.css'
export default function Numberbaseball() {
    const [word, setWord] = useState();
    const [inputWord, setInputWord] = useState("");
    const [numArray, setNumArray] = useState([0, 0, 0, 0, 0, 0, 0, 0, 0]);
    const [answerArray, setanswerArray] = useState([{
        Strike : 0,
        Ball : 0,
        Out : 0
    }])
    function MakeNumber() {
        setNumArray([0, 0, 0, 0, 0, 0, 0, 0, 0]);
        let WordArray = [];
        console.log("gd");
        for (let i = 1; i < 5;) {
            let iNum = Math.floor(Math.random() * (numArray.length - 1) + 1);
            if (numArray[iNum] === 0) {
                numArray[iNum] = i++;
                WordArray.push(iNum);
                console.log(numArray);
            }
        }
        setWord(WordArray);
        
        setanswerArray([{
            Strike : 0,
            Ball : 0,
            Out : 0
        }])
    }

    if (word === undefined) {
        MakeNumber();
    }

    function CheckNumber() {
        let arr = {
            Strike : 0,
            Ball : 0,
            Out : 0
        };
        for (let i = 0; i < 4; i++) {
            let iIdx = word.indexOf(Number(inputWord[i]));
            console.log("IDX : " + iIdx + "INPUT : " + inputWord[i]);
            switch (iIdx) {
                case i:
                    arr[0]++;
                    break;
                case -1:
                    arr[2]++;
                    break;
                default:
                    arr[1]++;
                    break;
            }
        }
        setanswerArray(answerArray.push(arr));
        
    }
    function onClickButtonAnswer() {
        CheckNumber();

    }
    function onClickButtonReset() {
        MakeNumber();
        //setInputWord("여기에 입력하여주세요!");
    }
    function onClickInput() {
        //setInputWord("");
    }
    function onChangeInput(e) {
        setInputWord(e.target.value);
    }
    function onKeyPress(e) {
        if (e.key === "Enter") {
            CheckNumber();
        }
    }
    return (
        <element id="Numberbaseball">
            <div>현재 숫자 :: {word}</div>
            <input onKeyPress={onKeyPress} value={inputWord} onChange={onChangeInput} maxLength='4' />
            <button onClick={onClickButtonAnswer}>GO!</button>
            <button onClick={onClickButtonReset}>초기화</button>
            {console.log("strike : " + answerArray.Strike + " " + answerArray.Ball + " " + answerArray.Out)}
            <div>{answerArray > 0 ? answerArray.map( p => {
                return p;
            }) : "0"}Strike!
            </div>
        </element>
    )
}
