function Nav() {
  return (
    <nav>
      <ul>
        <li><a href="localhost:3000">HOME</a></li>
        <li><button >김태형</button></li>
        <li>TEST1</li>
        <li>TEST2</li>
      </ul>
    </nav>
  )
}

function App(props) {
const [date, setDate] = useState("test");
let timeID;
function Change() {
  setDate(new Date().toLocaleTimeString());
  componentWIllUnmount();
}
function componentDidMount() {
  timeID = setInterval(
    () => Change(), 1000
  )
}

function componentWIllUnmount() {
  clearInterval(timeID)
}
  return (
    <div className="App">
      <h1>Hello React</h1>
    <h3>[{date}{componentDidMount()}]</h3>
      <Nav />
    </div>
  );
}

export default App;
