import React from 'react'
import { useState, useRef } from 'react'

import './Numberbaseball.css'
export default function Numberbaseball() {
    const [word, setWord] = useState();
    const [inputWord, setInputWord] = useState("");
    const [numArray, setNumArray] = useState([0, 0, 0, 0, 0, 0, 0, 0, 0]);
    const [strikeArray, setStrikeArray] = useState();
    const [ballArray, setBallArray] = useState();
    const [outArray, setOutArray] = useState();
    function MakeNumber() {
        setNumArray([0, 0, 0, 0, 0, 0, 0, 0, 0]);
        let WordArray = [];
        console.log("gd");
        for (let i = 1; i < 5;) {
            let iNum = Math.floor(Math.random() * (numArray.length - 1) + 1);
            if (numArray[iNum] === 0) {
                numArray[iNum] = i++;
                WordArray.push(iNum);
                console.log(numArray);
            }
        }
        setWord(WordArray);

        setStrikeArray();
        setBallArray();
        setOutArray();
    }

    if (word === undefined) {
        MakeNumber();
    }

    function CheckNumber() {
        let arr = [0, 0, 0];
        for (let i = 0; i < 4; i++) {
            let iIdx = word.indexOf(Number(inputWord[i]));
            console.log("IDX : " + iIdx + "INPUT : " + inputWord[i]);
            switch (iIdx) {
                case i:
                    arr[0]++;
                    break;
                case -1:
                    arr[2]++;
                    break;
                default:
                    arr[1]++;
                    break;
            }
        }
        strikeArray.push(arr[0]);
        ballArray.push(arr[1]);
        outArray.push(arr[2]);
        
    }
    function onClickButtonAnswer() {
        CheckNumber();

    }
    function onClickButtonReset() {
        MakeNumber();
        //setInputWord("여기에 입력하여주세요!");
    }
    function onClickInput() {
        //setInputWord("");
    }
    function onChangeInput(e) {
        setInputWord(e.target.value);
    }
    function onKeyPress(e) {
        if (e.key === "Enter") {
            CheckNumber();
        }
    }
    return (
        <element id="Numberbaseball">
            <div>현재 숫자 :: {word}</div>
            <input onClick={onClickInput} onKeyPress={onKeyPress} value={inputWord} onChange={onChangeInput} on maxLength='4' />
            <button onClick={onClickButtonAnswer}>GO!</button>
            <button onClick={onClickButtonReset}>초기화</button>
            <div>{strikeArray[0]}Strike! {ballArray[0]}Ball! {outArray[0]}Out!</div>
        </element>
    )
}
